import React from 'react';

//necesitamos las librerias del state y reselect para pasar las propiedades del state a ChekoutPage, y que llene la p치gina de los items que est치n en la compra
import { connect } from 'react-redux';
import { createStructuredSelector } from 'reselect';

import { selectCartItems, selectCartTotal } from '../../redux/cart/cart.selectors';
import './checkout.styles.scss';

const CheckoutPage = ({cartItems , total}) => (
  <div className='checkout-page'>
    <div className='checkout-header'>
      <div className='header-block'>
        <span>Product</span>
      </div>
      <div className='header-block'>
        <span>Description</span>
      </div>
      <div className='header-block'>
        <span>Quantity</span>
      </div>
      <div className='header-block'>
        <span>Price</span>
      </div>
      <div className='header-block'>
        <span>Remove</span>
      </div>
    </div>
    {
      cartItems.map(cartItem=>(<div key={cartItem.id}>{cartItem.name}</div>))
    }
    <div className='total'>
      Total: ${total}
    </div>
  </div>
);

const mapStateToProps = createStructuredSelector({
  cartItems:selectCartItems,
  total:selectCartTotal
})
export default connect(mapStateToProps)(CheckoutPage);
