import styled, {css} from 'styled-components';

import CustomButton from '../custom-button/custom-button.component';

export const ButtonContainer = styled(CustomButton)`
  width: 80%;
  opacity:0.7;
  position: absolute;
  top:255px;
  display: flex;

  &:hover {
    display:flex;
  }
`;
