import {takeLatest, put, all, call } from 'redux-saga/effects';

import { googleProvider, auth, createUserProfileDocument} from '../../firebase/firebase.utils';

import UserActionTypes from './user.types';

import { 
  googleSignInSuccess, 
  googleSignInFailure,
  emailSignInSuccess,
  emailSignInFailure
} from './user.actions';

//cuando se hace yield en onEmailSignInStart, se captura toda la action, y se tiene acceso como parametro al payload en este funci贸n de a continuaci贸n.
export function* signInWithEmail({payload:{ email, password }}){
  try{
//    const userRef= yield auth.signInWithEmailAndPassword(googleProvider);
// lo desestructuramos el objeto userRef, y cogemos la propiedad user
    const { user } = yield auth.signInWithEmailAndPassword(email, password);
    const userRef = yield call(createUserProfileDocument, user);
    const userSnapShot = yield userRef.get();
    console.log(userSnapShot);
    yield put (emailSignInSuccess({id:userSnapShot.id, ...userSnapShot.data()}));
  } catch(err){
    put(emailSignInFailure(err));
  }
}

export function* signInWithGoogle(){
  try{
//    const userRef= yield auth.signInWithPopup(googleProvider);
// lo desestructuramos el objeto userRef, y cogemos la propiedad user
    const { user } = yield auth.signInWithPopup(googleProvider);
    const userRef = yield call(createUserProfileDocument, user);
    const userSnapShot = yield userRef.get();
    console.log(userSnapShot);
    yield put (googleSignInSuccess({id:userSnapShot.id, ...userSnapShot.data()}));
  } catch(err){
    yield put (googleSignInFailure(err));
  }
}

export function* onEmailSignInStart(){
  yield takeLatest(UserActionTypes.EMAIL_SIGN_IN_START, signInWithEmail);
}

export function* onGoogleSignInStart(){
  yield takeLatest(UserActionTypes.GOOGLE_SIGN_IN_START, signInWithGoogle);
}

export function* userSagas(){
  yield all([call(onGoogleSignInStart),
    call(onEmailSignInStart)
  ]);
}
